document.addEventListener("DOMContentLoaded",function(){fetchPostData(),document.getElementById("like-button").addEventListener("click",function(){fetch(`/posts/${postID}/like`,{method:"POST",headers:{"Content-Type":"application/json"}}).then(t=>t.json()).then(t=>{document.getElementById("like-count").textContent=t.likes})}),document.getElementById("comment-form").addEventListener("submit",function(t){t.preventDefault();let e=document.getElementById("comment-content").value;fetch(`/posts/${postID}/comment`,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({content:e})}).then(t=>t.json()).then(t=>{document.getElementById("comments").innerHTML+=`
    <div class="comment">
        <p><strong>Anonymous User</strong>: ${t.content}</p>
    </div>
`,document.getElementById("comment-form").reset()})})});function fetchPostData(){fetch(`/posts/${postID}`).then(t=>t.json()).then(t=>{document.getElementById("like-count").textContent=t.likes;let e=document.getElementById("comments");e.innerHTML="",t.comments.forEach(t=>{e.innerHTML+=`
        <div class="comment">
            <p><strong>${t.author}</strong>: ${t.content}</p>
        </div>
    `})})}